(ns clojure-for-fun.leetcode.38-count-and-say-test
  (:require [clojure.test :refer :all])
  (:require [clojure-for-fun.leetcode.38-count-and-say :refer [countAndSay]]))

(deftest count-and-say-trans-test
  (testing "can-produce-correct-result"
    (is (= (countAndSay 10)
           "13211311123113112211"))
    (is (= (countAndSay 17)
           "11131221131211132221232112111312212321123113112221121113122113111231133221121321132132211331121321231231121113122113322113111221131221"))
    (is (= (countAndSay 21)
           ""))))
